(199, 39, 'exp_templates', 'template_data', 1419023372, 1, '<div class="gallery-listing small">\n{exp:channel:entries channel="gallery" dynamic="no" status="open" limit="5"}\n    {exp:polls:poll poll_id="{gallery_poll}"}\n        {poll_form}\n            <article class="gallery-item" data-poll-id="{poll_id}">\n                <div class="photo">\n                    <a href="{gallery_photo}" title="{gallery_photo_caption}" rel="gallery-listing">\n                        <img src="{gallery_photo}" alt="{title}" title="{title}" />\n                    </a>\n                    {if poll_is_open}\n                        <div class="poll-options">\n                            {if poll_limit_votes}\n                                {if has_voted}\n                                    <p class="already-voted">Thank you for voting!</p>\n                                {if:else}\n                                    {poll_options}\n                                        <a class="vote-{option_label} button" data-poll-option-id="{option_id}">{option_label}</a>\n                                    {/poll_options}\n                                {/if}\n                            {if:else}\n                                {poll_options}\n                                    <a class="vote-{option_label} button" data-poll-option-id="{option_id}">{option_label}</a>\n                                {/poll_options}\n                            {/if}\n                            {if poll_show_results}\n                                <a class="toggle-results button">View Results</a>\n                            {/if}\n                        </div>\n                    {/if}\n                </div>\n                <div class="content">\n                    <div class="details">\n                        <header>{title}</header>\n                        <p>{gallery_photo_caption}</p>\n                        {if poll_is_open}\n                            <p>{poll_question}</p>\n                        {/if}\n                    </div>\n                    {if poll_show_results}\n                        <div class="results">\n                            <header>Results for {title}</header>\n                            <div class="results-chart">\n                                <canvas id="poll-{poll_id}-results" width="150" height="150"></canvas>\n                            </div>\n                        </div>\n                    {/if}\n                </div>\n            </article>\n        {/poll_form}\n        {if poll_show_results}\n            <script>\n                $(function(){\n                    \n                    var results_data=[],\n                        i=1,\n                        colors=[''{primary_color}'',''{secondary_color}''],\n                        highlight_colors=[''{primary_hover_color}'',''{secondary_hover_color}''];\n\n                    {poll_options}\n                        results_data.push({\n                            value: {option_votes},\n                            label: ''{option_label}'',\n                            color: colors[ i % 2 ],\n                            highlight: highlight_colors[ i++ % 2 ]\n                        });\n                    {/poll_options}\n\n                    if(results_data.length)\n                    {\n                        var $canvas=$(''#poll-{poll_id}-results''),\n                            ctx=$canvas.get(0).getContext(''2d''),\n                            results_chart=new Chart(ctx).Pie(results_data,{\n                                legendTemplate: ''<ul class="<%=name.toLowerCase()%>-legend"><% for (var i=0; i<segments.length; i++){%><li><span style="background-color:<%=segments[i].fillColor%>"></span><%if(segments[i].label){%><%=segments[i].label%><%}%></li><%}%></ul>''\n                            }),\n                            legend_html=results_chart.generateLegend();\n\n                        $canvas.before(legend_html);\n                    }\n                });\n            </script>\n        {/if}\n    {/exp:polls:poll}\n{/exp:channel:entries}\n</div>'),
(136, 37, 'exp_templates', 'template_data', 1418395972, 1, '{layout="layout/.layout" body_id="gallery"}\n	{embed="layout/.parallax-img" image="gallery"}\n	{nav}\n	<main id="layout-content">\n		<h1>Gallery Templates</h1>\n		<p>These are the templates that will be used </p>\n		<h2>Homepage Live Feed</h2>\n		<div class="gallery-listing homepage">\n			{exp:channel:entries channel="gallery" dynamic="no" status="open"}\n<article class="gallery-item">\n    <div class="photo">\n        <a href="{gallery_photo}" title="{photo_caption}" rel="gallery-listing">\n            <img src="{gallery_photo}" alt="{title}" title="{title}" />\n        </a>\n        <div class="poll-options">\n            <a class="vote-for button" href="#">For</a>\n            <a class="vote-against button" href="#">Against</a>\n			<a class="toggle-results button">View Results</a>\n        </div>\n    </div>\n    <div class="content">\n        <div class="details">\n            <header>{title}</header>\n            <p>{gallery_photo_caption}</p>\n            <p>Are you for or against? Vote now!</p>\n        </div>\n        <div class="results">\n            <header>Results for {title}</header>\n            <img src="data:image/gif;base64,"/>\n        </div>\n    </div>\n</article>\n			{/exp:channel:entries}\n		</div>\n		<h2>Gallery Listing</h2>\n		<div class="gallery-listing">\n			<div class="pagination">\n				<a href="#" class="first-page">&laquo; First</a>\n				<a href="#" class="previous-page">&lsaquo; Previous</a>\n				<a href="#">1</a>\n				<a href="#" class="current-page">2</a>\n				<a href="#">3</a>\n				<a href="#">4</a>\n				<a href="#">5</a>\n				<a href="#" class="next-page">Next &rsaquo;</a>\n				<a href="#" class="last-page">Last &raquo;</a>\n			</div>\n			{exp:channel:entries channel="gallery" dynamic="no" status="open"}\n<article class="gallery-item">\n    <div class="photo">\n        <a href="{gallery_photo}" title="{photo_caption}" rel="gallery-listing">\n            <img src="{gallery_p