<table id="polls_listing" class="mainTable" border="0" cellspacing="0" cellpadding="0">	<thead>		<tr>			<th class="polls_listing_id"><?php echo lang('polls_listing_table_id'); ?></th>			<th class="polls_listing_question"><?php echo lang('polls_listing_table_question'); ?></th>			<th class="polls_listing_votes"><?php echo lang('polls_listing_table_votes'); ?></th>			<th class="polls_listing_status"><?php echo lang('polls_listing_table_status'); ?></th>			<th class="polls_listing_actions"><?php echo lang('polls_listing_table_actions'); ?></th>		</tr>	</thead>	<tbody>		<?php if( !empty($polls_listing) ) : ?>			<?php foreach( $polls_listing as $poll ) : ?>				<tr class="<?php echo alternator('even','odd'); ?>">					<td class="polls_listing_id"><?php echo $poll->id; ?></td>					<td class="polls_listing_question"><a href="<?php echo BASE.AMP.'C=addons_modules'.AMP.'M=show_module_cp'.AMP.'module=polls'.AMP.'method=show'.AMP.'poll='.$poll->id; ?>"><?php echo $poll->question; ?></a></td>					<td class="polls_listing_votes"><?php echo $poll->total_votes; ?></td>					<td class="polls_listing_status"><?php echo $poll->status; ?></td>					<td class="polls_listing_actions"><a href="<?php echo BASE.AMP.'C=addons_modules'.AMP.'M=show_module_cp'.AMP.'module=polls'.AMP.'method=show'.AMP.'poll='.$poll->id; ?>"><?php echo lang('polls_listing_stats'); ?> </a> | <a href="<?php echo BASE.AMP.'C=addons_modules'.AMP.'M=show_module_cp'.AMP.'module=polls'.AMP.'method=edit'.AMP.'poll='.$poll->id; ?>"><?php echo lang('polls_listing_edit'); ?> </a> | <a href="<?php echo BASE.AMP.'C=addons_modules'.AMP.'M=show_module_cp'.AMP.'module=polls'.AMP.'method=delete'.AMP.'poll='.$poll->id; ?>"><?php echo lang('polls_listing_delete'); ?> </a></td>				</tr>			<?php endforeach; ?>		<?php else : ?>			<tr class="even">				<td colspan="5"><?php echo lang('polls_listing_no_polls'); ?></td>			</tr>		<?php endif; ?>	</tbody></table>